mapper.select:
SELECT A.ARTICLE_ID as articleId , A.TITLE as title , A.DESCRIPTION as description , A.PUBLISH_FLG as publishFlg , A.PUBLISH_DATE as publishDate , C.RED_FLG as redFlg , C.TRANSLATE_FLG as translateFlg , NVL(V_ACCESS.CNT, 0) as accessCount , NVL(V_COLLECT.CNT, 0) as collectCount , NVL(V_COLLECT_2.CNT, 0) as collectFlg , NVL(V_COMMENT.CNT, 0) as commentCount , U.USER_ID as "userBean.userId" , U.NICKNAME as "userBean.nickname" , U.SIGNATURE as "userBean.signature" FROM M_ARTICLE A , R_COMIC C , M_USER U , (SELECT M_ACCESS.ARTICLE_ID , COUNT(M_ACCESS.ARTICLE_ID) as CNT FROM M_ACCESS WHERE M_ACCESS.DEL_FLG = '0' GROUP BY M_ACCESS.ARTICLE_ID ) V_ACCESS , (SELECT R_COLLECT.ARTICLE_ID , COUNT(R_COLLECT.ARTICLE_ID) as CNT FROM R_COLLECT WHERE R_COLLECT.DEL_FLG = '0' GROUP BY R_COLLECT.ARTICLE_ID ) V_COLLECT , (SELECT R_COLLECT.ARTICLE_ID , COUNT(R_COLLECT.ARTICLE_ID) as CNT FROM R_COLLECT WHERE R_COLLECT.DEL_FLG = '0'
AND R_COLLECT.USER_ID = #{collectUserId}
AND R_COLLECT.USER_ID is null
GROUP BY R_COLLECT.ARTICLE_ID ) V_COLLECT_2 , (SELECT M_ARTICLE_COMMENT.ARTICLE_ID , COUNT(M_ARTICLE_COMMENT.ARTICLE_ID) as CNT FROM M_ARTICLE_COMMENT WHERE M_ARTICLE_COMMENT.DEL_FLG = '0' GROUP BY M_ARTICLE_COMMENT.ARTICLE_ID ) V_COMMENT WHERE C.ARTICLE_ID = A.ARTICLE_ID (+) AND C.ARTICLE_ID = V_ACCESS.ARTICLE_ID (+) AND C.ARTICLE_ID = V_COLLECT.ARTICLE_ID (+) AND A.ARTICLE_ID = V_COLLECT_2.ARTICLE_ID (+) AND C.ARTICLE_ID = V_COMMENT.ARTICLE_ID (+) AND A.USER_ID = U.USER_ID (+)
AND C.ARTICLE_ID = #{articleId}
AND C.DEL_FLG = '0'

mapper.insert:
INSERT INTO R_COMIC(
    ARTICLE_ID
,   RED_FLG
,   TRANSLATE_FLG
,   ADD_DATE
,   UPD_DATE
,   DEL_FLG
) VALUES (
    #{articleId}
,   #{redFlg}
,   #{translateFlg}
,   SYSDATE
,   SYSDATE
,   '0'
)
