mapper.select:
SELECT count(m_article.id) FROM m_article left join r_music on r_music.id = m_article.id left join m_user on m_user.id = m_article.user_id
AND m_article.user_id = #{userBean.id}
AND m_article.publish_flg = #{publishFlg}
AND (m_article.title LIKE #{pattern} OR m_article.description LIKE #{pattern})
AND r_music.del_flg = '0'

SELECT
m_article.id as id, m_article.title as title, m_article.description as description, m_article.publish_flg as publishFlg, m_article.recommend_flg as recommendFlg, m_user.id as "userBean.id", m_user.nickname as "userBean.nickname",
FROM m_article left join r_music on r_music.id = m_article.id left join m_user on m_user.id = m_article.user_id
AND m_article.user_id = #{userBean.id}
AND m_article.publish_flg = #{publishFlg}
AND (m_article.title LIKE #{pattern} OR m_article.description LIKE #{pattern})
AND r_music.del_flg = '0'
order by id desc limit #{start}, #{pageSize}
