mapper.insert:
INSERT INTO M_USER(
EMAIL, PASSWORD, NICKNAME,
) VALUES (
#{email}, #{password}, #{nickname},
)

mapper.select:
SELECT
U.id as id, U.EMAIL as email, U.PASSWORD as password, U.NICKNAME as nickname, U.signature as signature, U.role as role, U.include_red_flg as includeRedFlg, U.only_translate_flg as onlyTranslateFlg, U.only_vocal_flg as onlyVocalFlg,
FROM M_USER U
U.DEL_FLG = '0'
AND U.fingerprint = #{fingerprint}
AND U.email = #{email}
AND U.id = #{id}
