mapper.insert:
INSERT INTO R_COLLECT(
    USER_ID
,   ARTICLE_ID
,   ADD_DATE
,   UPD_DATE
,   DEL_FLG
) VALUES (
    #{userId}
,   #{articleId}
,   SYSDATE
,   SYSDATE
,   '0'
)

mapper.select:
SELECT COUNT(C.USER_ID) FROM R_COLLECT C WHERE C.DEL_FLG = '0'
AND C.ARTICLE_ID = #{articleId}
AND C.USER_ID = #{userId}
