mapper.select:
SELECT
v_user.id as id, v_user.nickname as nickname,
FROM (SELECT m_auth_file.user_id, m_auth_file.upd_date FROM m_auth_file
AND m_auth_file.status = #{status}
AND m_auth_file.DEL_FLG='0'
group by m_auth_file.user_id) auth_file join v_user ON auth_file.user_id = v_user.id order by auth_file.upd_date desc limit #{start}, #{pageSize}

mapper.update:
UPDATE m_auth_file

    status = #{status}


    m_auth_file.id = #{authFileId}
AND m_auth_file.DEL_FLG='0'
