mapper.insert:
INSERT INTO m_comment(
ARTICLE_ID, USER_ID, content, parent_id, root_id,
) VALUES (
#{articleBean.id}, #{userBean.id}, #{content}, #{parent.id}, #{root.id},
)

mapper.select:
SELECT
v_comic_comment.id as id, v_comic_comment.content as content, v_comic_comment.createDate as createDate, v_comic_comment.`userBean.id` as `userBean.id`, v_comic_comment.`userBean.nickname` as "userBean.nickname", v_comic_comment.`articleBean.id` as `articleBean.id`, v_comic_comment.`articleBean.title` as `articleBean.title`,
FROM v_comic_comment
v_comic_comment.`articleBean.id` = #{articleId}
order by v_comic_comment.id desc limit #{start}, #{pageSize}

SELECT
v_music_comment.id as id, v_music_comment.content as content, v_music_comment.createDate as createDate, v_music_comment.`userBean.id` as `userBean.id`, v_music_comment.`userBean.nickname` as "userBean.nickname", v_music_comment.`articleBean.id` as `articleBean.id`, v_music_comment.`articleBean.title` as `articleBean.title`,
FROM v_music_comment
v_music_comment.`articleBean.id` = #{articleId}
order by v_music_comment.id desc limit #{start}, #{pageSize}
