mapper.insert:
INSERT INTO r_like(
USER_ID, LIKE_ID, CATEGORY,
) VALUES (
#{userBean.id}, #{likeId}, #{category},
)

mapper.select:
SELECT
    COUNT(L.ID)
FROM
    r_like L

    L.DEL_FLG = '0'
AND L.USER_ID = #{userBean.id}
AND L.LIKE_ID = #{likeId}
AND L.CATEGORY = #{category}

mapper.update:
UPDATE
    r_like L
SET
    L.DEL_FLG = '1'

AND L.USER_ID = #{userBean.id}
AND L.LIKE_ID = #{likeId}
AND L.CATEGORY = #{category}
