mapper.select:
SELECT 
    count(v_music.id)
FROM
    v_music

    
    AND v_music.`userBean.id` = #{userBean.id}
    
    
    AND v_music.`publishFlg` = #{publishFlg}
    
    
    
    AND (v_music.title LIKE #{pattern}
    OR  v_music.description LIKE #{pattern})

SELECT
v_music.id as id, v_music.title as title, v_music.description as description, v_music.`userBean.id` as `userBean.id`, v_music.`userBean.nickname` as `userBean.nickname`, v_music.accessCount as accessCount, v_music.likeCount as likeCount, v_music.commentCount as commentCount,
FROM v_music
AND v_music.`userBean.id` = #{userBean.id}
AND v_music.publishFlg = #{publishFlg}
AND (v_music.title LIKE #{pattern} OR v_music.description LIKE #{pattern})
order by v_music.id desc limit #{start}, #{pageSize}

SELECT
v_music_ranking.id as id, v_music_ranking.title as title, v_music_ranking.description as description, v_music_ranking.publishFlg as publishFlg, v_music_ranking.publishDate as publishDate, v_music_ranking.recommendFlg as recommendFlg, v_music_ranking.`userBean.id` as `userBean.id`, v_music_ranking.`userBean.nickname` as `userBean.nickname`, v_music_ranking.`userBean.signature` as `userBean.signature`, v_music_ranking.accessCount as accessCount, v_music_ranking.likeCount as likeCount, v_music_ranking.commentCount as commentCount, v_music_ranking.pt as pt
FROM v_music_ranking limit 0, #{pageSize}
