mapper.insert:
INSERT INTO r_user_library(
user_id, article_id,
) VALUES (
#{userBean.id}, #{articleBean.id},
)

mapper.select:
SELECT
count(v_music.id)
FROM v_music left join r_user_library on v_music.id = r_user_library.article_id
AND r_user_library.user_id = #{userBean.id}
AND r_user_library.article_id = #{articleBean.id}

SELECT
v_music.id as id, v_music.title as title,
FROM v_music left join r_user_library on v_music.id = r_user_library.article_id
AND r_user_library.user_id = #{userBean.id}
order by v_music.id desc limit #{start}, #{pageSize}
