mapper.insert:
INSERT INTO r_user_library(
user_id, article_id, status,
) VALUES (
#{userBean.id}, #{articleBean.id}, #{status},
)

mapper.select:
SELECT

    count(r_user_library.id)

FROM
    r_user_library

AND r_user_library.user_id = #{userBean.id}

AND r_user_library.article_id = #{articleBean.id}


AND r_user_library.status = #{status}

SELECT

    status,

FROM
    r_user_library

AND r_user_library.user_id = #{userBean.id}
AND r_user_library.article_id = #{articleBean.id}

SELECT

    count(v_music.id)

FROM
    v_music
left join r_user_library on v_music.id = r_user_library.article_id

AND r_user_library.user_id = #{userBean.id}

AND r_user_library.article_id = #{articleBean.id}


AND r_user_library.status = #{status}

SELECT
v_music.id as id, v_music.title as title,
FROM v_music left join r_user_library on v_music.id = r_user_library.article_id
AND r_user_library.user_id = #{userBean.id}
AND r_user_library.status = #{status}
order by v_music.id desc

SELECT

    v_music.id                      as id,
    v_music.title                   as title,

FROM
    v_music
left join r_user_library on v_music.id = r_user_library.article_id

AND r_user_library.user_id = #{userBean.id}

AND r_user_library.status = #{status}


order by v_music.id desc
limit #{start}, #{pageSize}

mapper.update:
update 
    r_user_library
set 
    r_user_library.status = #{status}

AND r_user_library.user_id = #{userBean.id}
AND r_user_library.article_id = #{articleBean.id}
